  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TINT
in vec4 a_color0;
out vec4 v_color0;
#endif
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#if USE_MULTI_TEXTURE
in float a_texId;
out float v_texId;
#endif
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
